# C1
def isSubSequence_c1(A, B):
    check = 0
    index = 0
    for i in range(len(A)):
        for j in range(index, len(B)):
            if (A[i] == B[j]):
                check += 1
                index = j + 1
                break
    # print(len(B))
    # print(len(A))
    # print(check)
    return 1 if check == len(A) else 0


